export const getFormParam = (e: any):{} => {
    const param = e.currentTarget.map((tag :any, i:number) => {
        if(tag.type ==='submit')return;
        return {
            [`${tag.name}`] : tag.value
        }});
        debugger;
        
    return param;
}
