import React, {useRef} from 'react';
import './imageButton.css';

function ImageButton({imageSrc, alt, css}) : any {

  const onClick = (e : any) => {
    let list = e.currentTarget.classList, flag = false;
    
    for(let i=0; i<list.length; i++) {
      flag = list[i] === 'ImageBtnActive' ? true : false;
    }
  }

  return (
      <button className="ImageBtn ImageBtnActive" style={css} onClick={onClick}>
        <img src={imageSrc} alt={alt}/>
      </button>
  );
}

export default ImageButton;
