import React, {useRef} from 'react';
import './imageButton.css';

function ImageButton({imageSrc, imageHover, alt, css, type}) : any {
  let url:string = 'this.src=';

  const onClick = (e : any) => {
    switch(type) {
      case 'friend' :
        url += imageHover;
        break;
      case 'chat' : 
        break;
    }
  }

  return (
      <button className="ImageBtn" style={css} onClick={onClick}>
        <img src={imageSrc} alt={alt}/>
      </button>
  );
}

export default ImageButton;
