import React, { useState } from 'react';
import {observer} from 'mobx-react';
import './Modal.css';

const Modal = observer(({opt}) => {
    const [msg, setMsg] = useState('ID를 등록하고 검색을 허용한 친구만 찾을 수 있습니다.');
    let show = opt;
    const onClick = () : void => {
        opt = false;
    }    
    
    return (
        <div style={show ? {display : 'block'} : {display : 'none'}}>
            <div className="maskingPage"></div>
            <div className="modalPage">
                {/* header */}
               <div className="modalPage_header">
                    <button className="modalPage_header_closeBtn" type="button" onClick={onClick}></button>
                    <h1 className="modalPage_header_h1">{opt.title}</h1>
                </div> 
                {/* body */}
                <span className="modalPage_body_span">ID로 추가</span>
                <hr className="line"></hr>
                <div className="modalPage_body">
                    <input className="modalPage_input" type="text"/>
                    <div className="modalPage_state">
                        {msg}
                    </div>
                </div>
            </div>
        </div>
    )
});


export default Modal;
